import React, { useEffect, useState } from 'react';
import useGame from '../../hooks/useGame';
import '../../styles/global.css';

const PLAYER_START_X = 375;
const PLAYER_START_Y = 500;

const GameBoard = () => {
    const { gameState, startGame } = useGame();
    const [playerPos, setPlayerPos] = useState({ x: PLAYER_START_X, y: PLAYER_START_Y });
    const [gameStarted, setGameStarted] = useState(false);
    const [paused, setPaused] = useState(false);

    useEffect(() => {
        if (!gameStarted || paused) return;
        const handleKeyDown = (e) => {
            setPlayerPos((pos) => {
                if (e.key === 'ArrowLeft') return { ...pos, x: Math.max(pos.x - 20, 0) };
                if (e.key === 'ArrowRight') return { ...pos, x: Math.min(pos.x + 20, 1100) };
                return pos;
            });
        };
        window.addEventListener('keydown', handleKeyDown);
        return () => window.removeEventListener('keydown', handleKeyDown);
    }, [gameStarted, paused]);

    const handleGameStart = () => {
        startGame();
        setPlayerPos({ x: PLAYER_START_X, y: PLAYER_START_Y });
        setGameStarted(true);
        setPaused(false);
    };

    const handlePause = () => {
        setPaused((prev) => !prev);
    };

    return (
        <div className="game-board" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <div
                className="game-area"
                style={{
                    position: 'relative',
                    width: '100%',
                    height: 700,
                    backgroundImage: "url('/assets/backgrounds/nyc.png')",
                    backgroundSize: 'cover',
                    backgroundPosition: 'center',
                    border: '4px solid #333',
                    borderRadius: '16px',
                    margin: '0 auto',
                    boxShadow: '0 0 40px rgba(0,0,0,0.5)'
                }}
            >
                <div
                    className="player"
                    style={{
                        position: 'absolute',
                        left: playerPos.x,
                        top: playerPos.y,
                        width: 200,
                        height: 200,
                        backgroundImage: "url('/assets/characters/last-bot.png')",
                        backgroundSize: 'cover'
                    }}
                />
                {gameStarted && !paused && <div style={{ position: 'absolute', top: 10, left: 10, color: 'green' }}>Game Started!</div>}
                {gameStarted && paused && <div style={{ position: 'absolute', top: 10, left: 10, color: 'orange' }}>Game Paused</div>}
            </div>
            {!gameStarted ? (
                <button
                    onClick={handleGameStart}
                    style={{
                        marginTop: 40,
                        width: 80,
                        height: 80,
                        borderRadius: '50%',
                        backgroundColor: 'limegreen',
                        color: '#fff',
                        border: 'none',
                        fontSize: '1.2rem',
                        fontWeight: 'bold',
                        cursor: 'pointer',
                        boxShadow: '0 4px 16px rgba(0,255,0,0.2)'
                    }}
                >
                    ▶
                </button>
            ) : (
                <button
                    onClick={handlePause}
                    style={{
                        marginTop: 20,
                        width: 50,
                        height: 50,
                        borderRadius: '50%',
                        backgroundColor: paused ? 'limegreen' : 'orange',
                        color: '#fff',
                        border: 'none',
                        fontSize: '1.2rem',
                        fontWeight: 'bold',
                        cursor: 'pointer',
                        boxShadow: '0 4px 16px rgba(0,255,0,0.2)'
                    }}
                >
                    {paused ? '▶' : '⏸'}
                </button>
            )}
            <div className="score-display">Score: {gameState.score}</div>
        </div>
    );
};

export default GameBoard;
