import React, { useEffect, useState } from 'react';
import Player from './Player';
import Clone from './Clone';
import PowerUp from './PowerUp';
import { useGame } from '../hooks/useGame';
import { useScore } from '../../hooks/useScore';
import { gameLogic } from '../../utils/gameLogic';
import '../../styles/global.css';

const GameBoard = () => {
    const { gameState, startGame, resetGame } = useGame();
    const { score, updateScore } = useScore();
    const [clones, setClones] = useState([]);
    const [powerUps, setPowerUps] = useState([]);

    useEffect(() => {
        if (gameState.isRunning) {
            const gameInterval = setInterval(() => {
                gameLogic.updateGame(clones, powerUps, updateScore);
            }, 1000 / 60); // 60 FPS

            return () => clearInterval(gameInterval);
        }
    }, [gameState.isRunning, clones, powerUps, updateScore]);

    const handleGameStart = () => {
        startGame();
        setClones(gameLogic.initializeClones());
        setPowerUps(gameLogic.initializePowerUps());
    };

    return (
        <div className="game-board">
            <button onClick={handleGameStart}>Start Game</button>
            <div className="game-area">
                <Player />
                {clones.map((clone, index) => (
                    <Clone key={index} clone={clone} />
                ))}
                {powerUps.map((powerUp, index) => (
                    <PowerUp key={index} powerUp={powerUp} />
                ))}
            </div>
            <div className="score-display">Score: {score}</div>
        </div>
    );
};

export default GameBoard;
