import React, { useEffect, useState } from 'react';
import useGame from '../../hooks/useGame';
import '../../styles/global.css';

const PLAYER_START_X = 375;
const PLAYER_START_Y = 500;

const GameBoard = () => {
    const { gameState, startGame } = useGame();
    const [playerPos, setPlayerPos] = useState({ x: PLAYER_START_X, y: PLAYER_START_Y });
    const [gameStarted, setGameStarted] = useState(false);

    useEffect(() => {
        const handleKeyDown = (e) => {
            setPlayerPos((pos) => {
                if (e.key === 'ArrowLeft') return { ...pos, x: Math.max(pos.x - 20, 0) };
                if (e.key === 'ArrowRight') return { ...pos, x: Math.min(pos.x + 20, 750) };
                return pos;
            });
        };
        window.addEventListener('keydown', handleKeyDown);
        return () => window.removeEventListener('keydown', handleKeyDown);
    }, []);

    const handleGameStart = () => {
        startGame();
        setPlayerPos({ x: PLAYER_START_X, y: PLAYER_START_Y });
        setGameStarted(true); // <-- Add this line
    };

    return (
        <div className="game-board">
            {!gameStarted && (
                <button onClick={handleGameStart}>Start Game</button>
            )}
            <div
                className="game-area"
                style={{
                    position: 'relative',
                    width: 1300,
                    height: 700,
                    backgroundImage: "url('/assets/backgrounds/nyc.png')", // <-- your image path here
                    backgroundSize: 'cover',
                    backgroundPosition: 'center',
                    border: '4px solid #333',
                    borderRadius: '16px',
                    margin: '0 auto',
                    boxShadow: '0 0 40px rgba(0,0,0,0.5)',
                    paddingTop: '10px'
                }}
            >
                <div
                    className="player"
                    style={{
                        position: 'absolute',
                        left: playerPos.x,
                        top: playerPos.y,
                        width: 200,
                        height: 200,
                        backgroundImage: "url('/assets/characters/last-bot.png')",
                        backgroundSize: 'cover'
                    }}
                />
                {/* Add clones and power-ups here later */}
                {gameStarted && <div style={{ position: 'absolute', top: 10, left: 10, color: 'green' }}>Game Started!</div>}
            </div>
            <div className="score-display">Score: {gameState.score}</div>
        </div>
    );
};

export default GameBoard;
