import React, { useEffect, useState } from 'react';
import useGame from '../../hooks/useGame';
import '../../styles/global.css';

const PLAYER_START_X = 375;
const PLAYER_START_Y = 500;

const INITIAL_CLONES = [
    { x: 1150, y: 510, id: 1 },
    { x: 800, y: 490, id: 2 },
    { x: 1000, y: 500, id: 3 }
];

const BULLET_SPEED = 20;

const GameBoard = () => {
    const { gameState, startGame } = useGame();
    const [playerPos, setPlayerPos] = useState({ x: PLAYER_START_X, y: PLAYER_START_Y });
    const [gameStarted, setGameStarted] = useState(false);
    const [paused, setPaused] = useState(false);
    const [clones, setClones] = useState([]);
    const [bullets, setBullets] = useState([]);

    // --- MOVE THIS FUNCTION OUTSIDE THE JSX ---
    const handleGameStart = () => {
        startGame();
        setPlayerPos({ x: PLAYER_START_X, y: PLAYER_START_Y });
        setClones(INITIAL_CLONES);
        setGameStarted(true);
        setPaused(false);
    };

    useEffect(() => {
         if (!gameStarted || paused) return;
         const handleKeyDown = (e) => {
         if (e.repeat) return; // Prevent holding down the key
            setPlayerPos((pos) => {
                if (e.key === 'ArrowLeft') return { ...pos, x: Math.max(pos.x - 20, 0) };
                if (e.key === 'ArrowRight') return { ...pos, x: Math.min(pos.x + 20, 1000) };
            return pos;
        });
        if (e.code === 'Space') {
            setBullets((prev) => [
                ...prev,
                { x: playerPos.x + 90, y: playerPos.y, id: Date.now() }
            ]);
            e.preventDefault(); // Prevent default space bar behavior
        }
    };
        window.addEventListener('keydown', handleKeyDown);
        return () => window.removeEventListener('keydown', handleKeyDown);
    }, [gameStarted, paused, playerPos]);

    useEffect(() => {
        if (!gameStarted || paused) return;
        const interval = setInterval(() => {
            setBullets((prev) =>
                prev
                    .map(bullet => ({ ...bullet, x: bullet.x + BULLET_SPEED })) // Move right
                    .filter(bullet => bullet.x < 1200) // Remove if off screen (adjust for your game-area width)
            );
        }, 50);
        return () => clearInterval(interval);
    }, [gameStarted, paused]);

    useEffect(() => {
        if (!gameStarted || paused) return;
        setClones((prevClones) =>
            prevClones.filter(clone => {
                const hit = bullets.some(
                    bullet =>
                        bullet.x > clone.x &&
                        bullet.x < clone.x + 200 &&
                        bullet.y > clone.y &&
                        bullet.y < clone.y + 200
                );
                return !hit;
            })
        );
    }, [bullets, gameStarted, paused]);

    const handlePause = () => {
        setPaused((prev) => !prev);
    };

  // Ref for the game area
    const gameAreaRef = React.useRef(null);

    // Full screen handler
    const handleFullScreen = () => {
        if (gameAreaRef.current) {
            if (gameAreaRef.current.requestFullscreen) {
                gameAreaRef.current.requestFullscreen();
            } else if (gameAreaRef.current.webkitRequestFullscreen) {
                gameAreaRef.current.webkitRequestFullscreen();
            } else if (gameAreaRef.current.mozRequestFullScreen) {
                gameAreaRef.current.mozRequestFullScreen();
            } else if (gameAreaRef.current.msRequestFullscreen) {
                gameAreaRef.current.msRequestFullscreen();
            }
        }
    };

    const [isFullScreen, setIsFullScreen] = useState(false);

// ...existing code...

useEffect(() => {
    const handleFullScreenChange = () => {
        setIsFullScreen(
            document.fullscreenElement === gameAreaRef.current ||
            document.webkitFullscreenElement === gameAreaRef.current ||
            document.mozFullScreenElement === gameAreaRef.current ||
            document.msFullscreenElement === gameAreaRef.current
        );
    };
    document.addEventListener('fullscreenchange', handleFullScreenChange);
    document.addEventListener('webkitfullscreenchange', handleFullScreenChange);
    document.addEventListener('mozfullscreenchange', handleFullScreenChange);
    document.addEventListener('MSFullscreenChange', handleFullScreenChange);
    return () => {
        document.removeEventListener('fullscreenchange', handleFullScreenChange);
        document.removeEventListener('webkitfullscreenchange', handleFullScreenChange);
        document.removeEventListener('mozfullscreenchange', handleFullScreenChange);
        document.removeEventListener('MSFullscreenChange', handleFullScreenChange);
    };
}, []); // <-- add missing dependency array

// ...existing code...
// ...existing code...
return (
    <div className="game-board" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <div
            ref={gameAreaRef}
            className="game-area"
            style={{
                position: 'relative',
                width: 1200,
                height: 700,
                backgroundImage: "url('/assets/backgrounds/nyc.png')",
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                border: '4px solid #333',
                borderRadius: '16px',
                margin: '0 auto',
                boxShadow: '0 0 40px rgba(0,0,0,0.5)'
            }}
        >
            {/* Instructions overlay */}
            {!gameStarted && (
                <div
                    style={{
                        position: 'absolute',
                        top: '20%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        background: 'rgba(34,34,34,0.95)',
                        color: 'limegreen',
                        borderRadius: '16px',
                        padding: '32px 48px',
                        fontSize: '1.4rem',
                        textAlign: 'center',
                        zIndex: 20,
                        boxShadow: '0 4px 32px rgba(0,255,0,0.12)'
                    }}
                >
                    <div><strong>Controls</strong></div>
                    <div style={{ marginTop: 12 }}>← Left Arrow: Move Left</div>
                    <div>→ Right Arrow: Move Right</div>
                    <div>Space Bar: Shoot</div>
                </div>
            )}
            {/* Centered Play Button */}
            {!gameStarted && (
                <button
                    onClick={handleGameStart}
                    style={{
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        width: 100,
                        height: 100,
                        borderRadius: '50%',
                        backgroundColor: 'limegreen',
                        color: '#fff',
                        border: 'none',
                        fontSize: '2rem',
                        fontWeight: 'bold',
                        cursor: 'pointer',
                        boxShadow: '0 4px 16px rgba(0,255,0,0.2)',
                        zIndex: 30
                    }}
                >
                    ▶
                </button>
            )}
            {/* Bullets, player, clones, etc. */}
            {bullets.map(bullet => (
                <div
                    key={bullet.id}
                    style={{
                        position: 'absolute',
                        left: bullet.x,
                        top: bullet.y,
                        width: 16,
                        height: 32,
                        backgroundColor: 'limegreen',
                        borderRadius: '8px'
                    }}
                />
            ))}
            <div
                className="player"
                style={{
                    position: 'absolute',
                    left: playerPos.x,
                    top: isFullScreen ? playerPos.y * 1.8 : playerPos.y,
                    width: 200,
                    height: 200,
                    backgroundImage: "url('/assets/characters/last-bot.png')",
                    backgroundSize: 'cover'
                }}
            />
            {clones.map(clone => (
                <div
                    key={clone.id}
                    className="clone"
                    style={{
                        position: 'absolute',
                        left: clone.x,
                        top: isFullScreen ? clone.y * 1.8 : clone.y,
                        width: 200,
                        height: 200,
                        backgroundImage: "url('/assets/characters/enemy-clone.png')",
                        backgroundSize: 'cover'
                    }}
                />
            ))}
            {gameStarted && !paused && <div style={{ position: 'absolute', top: 10, left: 10, color: 'green' }}>Game Started!</div>}
            {gameStarted && paused && <div style={{ position: 'absolute', top: 10, left: 10, color: 'orange' }}>Game Paused</div>}
        </div>
        {/* Pause and Full Screen Buttons Side by Side */}
        <div style={{ display: 'flex', gap: 24, marginTop: 24 }}>
            {gameStarted && (
                <button
                    onClick={handlePause}
                    style={{
                        width: 80,
                        height: 80,
                        borderRadius: '12px',
                        backgroundColor: paused ? 'limegreen' : 'orange',
                        color: '#fff',
                        border: 'none',
                        fontSize: '2rem',
                        fontWeight: 'bold',
                        cursor: 'pointer',
                        boxShadow: '0 4px 16px rgba(0,255,0,0.2)',
                        padding: 0
                    }}
                >
                    {paused ? '▶' : '⏸'}
                </button>
            )}
            <button
                onClick={handleFullScreen}
                style={{
                    width: 80,
                    height: 80,
                    borderRadius: '12px',
                    backgroundColor: '#222',
                    color: 'limegreen',
                    border: '2px solid limegreen',
                    fontSize: '1.2rem',
                    fontWeight: 'bold',
                    cursor: 'pointer',
                    boxShadow: '0 4px 16px rgba(0,255,0,0.1)',
                    padding: 0
                }}
            >
                Full<br />Screen
            </button>
        </div>
        <div className="score-display" style={{ marginTop: 16 }}>Score: {gameState.score}</div>
    </div>
    );
// ...existing code...
// filepath: /Users/mel/Desktop/last-bot-game/src/components/Game/GameBoard.jsx
};

export default GameBoard;
